namespace LeniaBeauty{using UnityEngine;using UnityEngine.UI;public class LeniaBeautyInstall:MonoBehaviour{[RuntimeInitializeOnLoadMethod(RuntimeInitializeLoadType.AfterSceneLoad)]static void Install(){var go=new GameObject("LeniaBeautyUIInstaller");UnityEngine.Object.DontDestroyOnLoad(go);go.AddComponent<LeniaBeautyInstall>();}void Start(){Apply();}void Apply(){var cam=Camera.main;if(cam!=null){cam.allowHDR=true;cam.clearFlags=CameraClearFlags.SolidColor;cam.backgroundColor=new Color(0.01f,0.02f,0.04f,1f);}}}}
